import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  constructor(private userService: UserService, private router: Router) { }

  user = {
    username: '',
    password: '',
  };

  login() {
    this.userService.login(this.user).subscribe((res) => {
      console.log(res);

      if (res != null) {

        if (res.role === 'admin') {
          this.router.navigateByUrl('admin-dashboard');
        } else {
          this.router.navigateByUrl('faculty-dashboard');
        }

      }
    })
  }
}


<div class="login-container">
  <div class="login-box">
    <h2>Login</h2>
    <form (ngSubmit)="login()">
      <div class="input-group">
        <label for="username">Username</label>
        <input type="text" id="username" placeholder="Enter your username" name="username" [(ngModel)]="user.username" required>
      </div>
      <div class="input-group">
        <label for="password">Password</label>
        <input type="password" id="password" placeholder="Enter your password" name="password" [(ngModel)]="user.password" required>
      </div>
      <button type="submit" class="login-btn">Login</button>
      <div *ngIf="loginError" class="error-message">Invalid username or password. Please try again.</div>
    </form>
  </div>
</div>
